mapper.update:
UPDATE chat_forestfull.member
SET profile_image = #{profileImageUrl}
WHERE id = #{id}

mapper.select:
SELECT COUNT(0) > 0
FROM chat_forestfull.member
WHERE display_name = #{nickname}
  AND id != #{userId}

SELECT COUNT(0) > 0
FROM chat_forestfull.member
WHERE email = #{email}
  AND id != #{userId}

mapper.update:
UPDATE chat_forestfull.member

    updated_at = NOW(),

        display_name = #{newNickname},


        email = #{newEmail},


        profile_image = #{newProfileImageUrl}


WHERE id = #{userId}
